INSERT INTO `Theses` (`id`, `concept_id`, `theses`, `class`, `page_code`, `day_start`, `month_start`, `year_start`, `day_end`, `month_end`, `year_end`) VALUES
(1, 3, 'З часів появи слов’ян до утворення Київської Русi пройшло декiлька столiть. У цей перiод державнiсть схiдних слов’ян розвивалася шляхом утворення так званих племiнних союзiв. Це були об’єднання племен та родiв пiд проводом патрiархiв зi спiльними вiруваннями.', 'Загальне твердження', 'Utvorennya-Kyyivskoyi-Rusi', 0, 0, 800, 0, 0, 0),
(2, 5, 'прямі нащадки Кия, брати, які правили разом.', 'Загальне твердження', 'Zasnuvannya-Kyyeva', 0, 0, 0, 0, 0, 0),
(3, 5, 'Версія 2 - бояри Рюрика, варяга і новгородського князя. Подорожуючи у Візантію, за наказом суверена зупинилися у Києві і почали тут княжити. У 882 році, після смерті Рюрика, Олег – регент свого нащадка Ігоря – йде походом на Київ і, хитрощами виманивши братів з-за київського муру, вбиває їх.', 'Загальне твердження', 'Zasnuvannya-Kyyeva', 0, 0, 0, 0, 0, 0),
(4, 5, 'Оскільки згадки про Діра досить малочисельні, дехто вважає, що Аскольд і Дір — це одна людина. На користь цієї версії говорить те, що відомим і успішним походом керував, відповідно до арабських хронік, лише Аскольд, хоча Дір мав би бути рівнозначною фігурою.', 'Загальне твердження', 'Zasnuvannya-Kyyeva', 0, 0, 0, 0, 0, 0),
(5, 5, 'У 860 році під проводом Аскольда руське військо у кількості 200-360 човнів і 6-10 тисяч воїнів висадилися в околицях Константинополя і з червня до липня спочатку тримали його в облозі, а потім — спустошували околиці. Як наслідок, пожвавилися торгівельні і культурні зв’язки Русі і Візантії.', 'Загальне твердження', 'Zasnuvannya-Kyyeva', 0, 0, 0, 0, 0, 0),
(6, 5, 'Саме візантійським впливом пояснюють напівлегендарне Аскольдове хрещення Русі наприкінці 860-х років. У 882 році вони були вбиті після облоги Києва новгородським військом з Олегом на чолі.', 'Загальне твердження', 'Zasnuvannya-Kyyeva', 0, 0, 0, 0, 0, 0),
(8, 6, 'У 882 році на чолі варязького війська увійшов до Києва, убивши Аскольда і Діра та де-факто започаткувавши династію Рюриковичів.', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 882, 0, 0, 0),
(9, 6, 'Правив князь-регент з 882 до 912, хоча і мав би передати трон Ігорю після досягнення останнім повноліття. Авторитет, військовий та політичний талант були вірними супутниками Олега, який отримав від нащадків прізвисько «Віщий».', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 882, 0, 0, 912),
(10, 6, 'Найбільших успіхів досяг у Візантії. Відомо два великих походи на Константинополь – у 907 та 911 роках.', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 907, 0, 0, 911),
(11, 4, 'Найбільших успіхів Олег досяг у Візантії. Відомо два великих походи на Константинополь – у 907 та 911 роках. Перший похід не викликає довіри у істориків, деякі навіть вважають його повністю вигадкою Нестора-літописця – автора «Повісті минулих років», яка містить детальну інформацію про розвиток цих подій. Згідно з «Повістю...» 2000 лодій підпливли до Константинопольскої гавані, яка, за наказом імператора Льва IV Філософа, була перекрита ланцюгами.', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 907, 0, 0, 0),
(12, 6, 'У 912 році здійснюється похід на Арабський халіфат. Пройшовши із Азовського моря у гирло Дону з дозволу Хозарського каганату, руський флот із 500 човнів досяг Волги, протягнувши свої лодії волоком по суходолу, звідки вони спустилися у Каспійське море. Спустошивши його південно-західне узбережжя, Олег направився у Русь, проте хозари вирішили помститися за одновірців і напали на руське військо, знищивши його основну частину.', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 912, 0, 0, 0),
(13, 7, '&lt;p&gt;Cин Рюрика, що став князювати не після досягнення повноліття, а лише після смерті князя-регента, приблизно у 33 роки (912 рік).&lt;/p&gt;\r\n', 'Визначення', 'Ihor-(bl-879945)', 0, 0, 0, 0, 0, 0),
(14, 7, 'Своє правління «молодий» князь розпочав із придушення повстань древлян та уличів.', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 0, 0, 0, 0),
(15, 7, '941 року флотилія із 1000 лодій (зустрічаються неправдоподібні дані про 10000 лодій) безперешкодно увійшла до Босфору у надії на те, що візантійський флот буде у поході проти сарацинів.', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 941, 0, 0, 0),
(16, 7, 'Після успіху у Візантії Ігор направив свою міць на каспійського союзника греків – Кавказьку Албанію. Спустошивши столицю й інші великі мусульманські міста, у тому ж 943 році, русичі зі славою та великою здобиччю повернулися додому, а їх вплив у Прикаспії зріс.', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 943, 0, 0, 0),
(17, 7, 'Здобуваючи вплив далеко за межами Русі, Ігор втрачав свій вплив на народи всередині країни, зокрема на волелюбних древлян. Збільшення данини суттєво бентежило їх, проте суперечки із київськими збирачами вирішувалися хоч і не мирним шляхом, але у межах здорового глузду.', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 945, 0, 0, 0),
(18, 7, 'І саме вихід за рамки, втрата почуття міри і спричинили вбивство князя Ігоря. Досить жорстоке вбивство, якщо знову ж таки вірити «Повісті минулих літ». Повернувшись із Каспійського походу, Ігор вирішив сам зібрати підвищену дань з древлян. Раніше це була прерогатива його воєводи Свенельда. Переконавши древлян за допомогою своєї дружини віддати необхідні кошти, Ігор попрямував до Києва, але, відпустивши більшість свого війська, повернувся до Іскоростеня – головного міста древлян, аби зібрати данину для себе особисто. Це викликало обурення у древлян на чолі з їх ватажком Малом, і вирішили вони покінчити з цим свавіллям.', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 945, 0, 0, 0),
(19, 8, '&lt;p&gt;Дружина князя Ігоря та мати князя Святослава.&lt;/p&gt;\r\n', 'Визначення', 'Olha-(bl.-890-969)', 0, 0, 890, 0, 0, 0),
(20, 8, '&lt;p&gt;Була княгинею-регентом після вбивства чоловіка (з 945 по 962 роки).&lt;/p&gt;\r\n', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 890, 0, 0, 0),
(21, 8, 'Відома через свою криваву помсту древлянам за князя Ігоря та як княгиня, що прийняла християнство та намагалася зробити його державною релігією Русі.', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 945, 0, 0, 0),
(22, 8, 'Вважається першою правителькою Русі, що офіційно прийняла християнство.', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 946, 0, 0, 0),
(23, 8, 'Вона досить цілеспрямовано готувалася до введення християнства на Русі як державної релігії, проте це доробить її онук.', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 946, 0, 0, 0),
(24, 8, 'Княгиня Ольга була не лише жорстокою (що було нормою Середньовіччя), але й мудрою. ', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 946, 0, 0, 0),
(25, 8, 'Помстившись за чоловіка, вона почала вирішувати проблему, яка стала причиною його смерті,— систему збору данини. Основою реформи стала чітка регламентація зборів за планом «хто, коли і скільки має сплатити».', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 946, 0, 0, 0),
(26, 8, 'На початку 960-х років нарешті і де-факто влада перейшла до Святослава Ігоровича, а Ольга лишалася таким собі мудрим порадником і помічником.', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 960, 0, 0, 0),
(27, 8, 'Так, наприклад, у 968 році Ольга керувала обороною Києва від печенігів, бо Святослав перебував у своїй болгарській резиденції.', 'Загальне твердження', 'Olha-(bl.-890-969)', 0, 0, 968, 0, 0, 0),
(28, 8, 'І хоча син не поділяв релігійних поглядів своєї матері, княгиня Ольга була похована за християнським звичаєм 24 липня 969 року.', 'Загальне твердження', 'Olha-(bl.-890-969)', 24, 7, 969, 0, 0, 0),
(29, 9, '&lt;p&gt;Князь-лицар, князь-козак, воїн, а не політик, побратим, а не володар, Олександр Македонський середньовіччя &amp;ndash; якими тільки епітетами не наділяли Святослава Ігоровича сучасники та історики.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 935, 0, 0, 0),
(30, 9, '&lt;p&gt;Пізно зійшовши на київський престол (тривалість регентства на Русі, як бачимо, залежить від регента, а не наступника), цей князь перекроїв карту Європи, змінив розклад сил у ній, пройшов із походами більше 8000(!) кілометрів&amp;nbsp;і загинув біля Дніпровських порогів від підступної засідки. І це за 10-12 років!&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 935, 0, 0, 0),
(31, 9, '&lt;p&gt;Після смерті своєї матері князь залишив у Києві старшого сина Ярополка, середній Олег став древлянським князем, а молодший Володимир сів у Новгороді. Влада була розділена, і Святослав міг продовжувати свої походи.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 935, 0, 0, 0),
(32, 9, '&lt;p&gt;Виношував мрію про дніпровсько-дунайську імперію зі столицею у Переяславці на Дунаї, а для кращого керівництва Руссю він вирішив розділити її поміж синами.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 935, 0, 0, 0),
(33, 9, 'Могутнє руське військо досить швидко не лише повернуло князеві втрачені болгарські землі, а й просунулося вглиб Болгарії. Проте війна, що мала бути русько-болгарською, перетворилася на русько-візантійську.', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 970, 0, 0, 0),
(34, 9, 'Святослав на хвилі успіху увійшов до візантійської провінції Фракії і рушив у напрямку Константинополя. За 100 км від столиці Візантійської імперії, поблизу міста Аркадіополь, відбулася битва між двома арміями, у якій греки зазнали значних втрат.', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 970, 0, 0, 0),
(35, 9, 'Святослав отримав величезну данину та військову славу, а імператор ліквідував загрозу для Константинополя, підписавши мирний договір. Сталося це 970 року.', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 970, 0, 0, 0),
(36, 9, 'Повертаючись навесні 972 року до Києва, князь із невеличким загоном потрапив у печенізьку засідку поблизу дніпровських порогів.', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 972, 0, 0, 0),
(37, 9, 'Київ так і не дочекався свого правителя, який змінив дніпровські кручі на дунайські береги.', 'Загальне твердження', 'Svyatoslav-(934-972)', 0, 0, 972, 0, 0, 0),
(38, 10, 'Почав своє правління у 972 році, посівши київський престол як найстарший із Святославовичів.', 'Загальне твердження', 'Yaropolk-(956-980)', 0, 0, 972, 0, 0, 0),
(39, 10, 'До 977 року ситуація у країні була стабільна: Ярополк намагався будувати дипломатичні відносини із Західною Європою і Священною Римською імперією почав карбування монети на Русі', 'Загальне твердження', 'Yaropolk-(956-980)', 0, 0, 977, 0, 0, 0),
(40, 10, '&lt;p&gt;Конфлікт розпочався між Ярополком та Олегом чи то через якісь матеріально-територіальні претензії, чи, скоріше за все, через політичні амбіції двох молодих князів.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaropolk-(956-980)', 0, 0, 977, 0, 0, 0),
(41, 10, 'Улітку 977 року Ярополк зі своєю армією висунувся у напрямку резиденції Олега — міста Вручия. У процесі битви середній Святославович упав з коня у рів і був розчавлений тілами.', 'Загальне твердження', 'Yaropolk-(956-980)', 0, 0, 977, 0, 0, 0),
(42, 11, '&lt;p&gt;Великий, Хреститель, Святий, Красне Сонечко &amp;ndash; якими тільки епітетами не нагороджували молодшого Святославовича сучасники, нащадки та історики.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(43, 11, 'Почав княжити у 980 році.', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 980, 0, 0, 0),
(44, 11, '&lt;p&gt;Найпершим його здобутком на престолі стало об&amp;rsquo;єднання усіх земель східних слов&amp;rsquo;ян в одній державі.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(45, 11, '981 року була завойована Червона Русь із містами Червеном та Перемишлем;', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 981, 0, 0, 0),
(46, 11, '982 – остаточно приєднані до Русі в’ятичі, яких обклав даниною ще батько;', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 982, 0, 0, 0),
(47, 11, '983 – приєднано землі балтійського племені ятвягів;', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 983, 0, 0, 0),
(48, 11, '984 – завойовано землі радимичів;', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 984, 0, 0, 0),
(49, 11, '985 – князь розпочинає військові дії проти Волзької Булгарії. Війна точиться зі змінним успіхом, проте мир підписаний на умовах, вигідніших болгарам. Того ж року данню обкладається занепадаюча Хазарія;', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 985, 0, 0, 0),
(50, 11, '988 року розпочинається так звана Корсунська війна. Після тривалої облоги і допомоги зрадників-містян Херсонес (Корсунь) здався, а руським військом були загарбані великі території Криму, аж до Керченського півострова.', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 988, 0, 0, 0),
(51, 11, '991 — після походу у Прикарпаття у складі Русі опиняються білі хорвати.', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 991, 0, 0, 0),
(52, 11, '&lt;p&gt;У результаті 10 років військових дій усі східнослов&amp;rsquo;янські племена об&amp;rsquo;єдналися у межах однієї держави та остаточно сформувалася територія Київської Русі. Вона стала найбільшою державою в Європі із площею більше 800 тис. км2.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(53, 11, '&lt;p&gt;Почав масштабно та систематизовано карбувати монету.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(54, 11, '&lt;p&gt;Запровадив військову&amp;nbsp;реформу, яка також мала посилити централізацію влади.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(55, 11, '&lt;p&gt;Активно розвивається камінне будівництво, хоча й не уціліло жодної будівлі того часу.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, NULL, 0, 0),
(56, 11, 'Закладаються міста Володимир на Клязьмі (990), Бєлгород (991) та інші.', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 990, 0, 0, 0),
(57, 11, '&lt;p&gt;ігноруючи принцип престолонаслідування, він вирішив передати Київ свому улюбленному сину Глібу. Звісно, це викликало непокору у старших синів Святополка та Ярослава.&lt;/p&gt;\r\n', 'Загальне твердження', 'Volodymyr-(958-1015)', 0, 0, 959, 0, 0, 0),
(58, 11, 'збирає каральний похід на Новгород, стольний град Ярослава, проте при підготовці до нього помирає 15 липня 1015 року у своїй заміській резиденції.', 'Загальне твердження', 'Volodymyr-(958-1015)', 15, 7, 1015, 0, 0, 0),
(59, 11, 'Тіло князя тишком вивезли, закутане у килими, та поховали у Десятинній церкві, яку сам і наказав побудувати на честь хрещення Русі.', 'Загальне твердження', 'Volodymyr-(958-1015)', 15, 7, 1015, 0, 0, 0),
(60, 12, '&lt;p&gt;Від його руки загинуло троє Володимировичів: Борис, Гліб та Святослав. За це у літописах отримав прізвисько &amp;laquo;Окаянний&amp;raquo;.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 982, 0, 0, 0),
(61, 12, 'у 1015 році став князем київським. Значних політичних досягнень його князювання не принесло.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 1015, 0, 0, 0),
(62, 12, '&lt;p&gt;Він встиг лише почати карбування власної монети, на кшталт Володимирової, та налагодити дипломатичні відносини з польськім королем Болеславом І Хоробрим, що був йому тестем.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 982, 0, 0, 0),
(63, 12, '&lt;p&gt;Усі чотири роки князювання були пов&amp;rsquo;язані із боротьбою проти Ярослава, адже Мстислав поки активно у боротьбу за владу не втручався.&lt;/p&gt;\r\n', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 982, 0, 0, 0),
(64, 12, 'Уже 1016 року Ярослав зі своєю дружиною виступає із Новгороду.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 1016, 0, 0, 0),
(65, 12, 'Біля міста Любеч, на берегах Дніпра, відбулася перша битва між братами. Перша сутичка лишилася за Ярославом.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 1017, 0, 0, 0),
(66, 12, 'Польсько-печенізьке військо Святополка зустрілося з дружиною Ярослава понад Західним Бугом у липні 1018 року. Військо останнього було вщент розбите, а сам претендент на престол втік до Новгороду.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 7, 1018, 0, 0, 0),
(67, 12, 'У 1019 році два брати знов зустрічаються у битві поблизу річки Альти.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 1019, 0, 0, 0),
(68, 12, 'Про битву на Альті інформації майже не лишилося, але відомо, що війська Ярослава розгромно перемогли, а Святополк втік у напрямку Польщі і загинув по дорозі чи то від ран, чи то втративши глузд.', 'Загальне твердження', 'Svyatopolk-(981-1019)', 0, 0, 1019, 0, 0, 0),
(69, 13, '&lt;p&gt;На відміну від батька, не вів загарбницьку політику, а зосередився на внутрішніх проблемах країни &amp;ndash; збереженні єдності та захисту кордонів.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(70, 13, 'Живим лишався брат Ярослава – Мстислав, князь тмутараканський, який все ще був претендентом на київський престол. У 1023 році він збирає військо і йде на Ярослава. Проте, зайнявши Чернігів, Мстислав не поспішає дати генеральну битву своєму братові.', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1023, 0, 0, 0),
(71, 13, '&lt;p&gt;було запропоновано, а надалі &amp;ndash; реалізовано, дуумвірат Володимировичів. Мстислав посів у Чернігові, а Ярослав вирішив лишитися у Новгороді, вважаючись князем київським.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(72, 13, '1029 року відбувся похід на ясів, що загрожували Тмутаракані;', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1029, 0, 0, 0),
(73, 13, '1030-1031 роки – польська кампанія, у якій Володимировичі підтримували князя Безприма у його зазіханнях на польський престол та водночас відвоювали Червону Русь;', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1030, 0, 0, 1031),
(74, 13, '1036 року військо Ярослава остаточно розбило сили печенігів, які так довго шматували кордони держави. На честь цієї перемоги було закладено собор Святої Софії;', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1036, 0, 0, 0),
(75, 13, '1043 року відбувся останній похід русів на Константинополь, яким керував син Ярослава – Володимир. І хоча руське військо зазнало поразки, відносини між Києвом та Візантією продовжують тіснішати.', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1043, 0, 0, 0),
(76, 13, '&lt;p&gt;Активно закладалися міста, здебільшого у прикордонні: більше 13 поселень було засновано понад Россю, Сулою та Трубежем &amp;ndash; південними кордонами Русі (Корсунь, Біла Церква, Торчеськ, Лубни);&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(77, 13, '&lt;p&gt;Також активізувалося будівництво безпосередньо у містах: розростаються Київ, Чернігів, Турів, Переяслав, Володимир-Волинський.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(78, 13, '&lt;p&gt;Засновано Києво-Печерську лавру, Юріїв монастир у Новгороді, Ірининський собор, Золоті ворота та Лядську браму у Києві.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(79, 13, '&lt;p&gt;За словами єпископа Саксонії Тітмара, у Києві того часу проживало більше 50 000 тисяч жителів та було більше 400 церков.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(80, 13, '&lt;p&gt;Продовжуючи справу батька, усіляко допомагав і стимулював розвиток християнства на Русі.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(81, 13, 'А у 1051 році князь зважується на безпрецедентний крок: власноруч, без дозволу константинопольського патріарха, затвердив митрополита київського. Ним став Іларіон – перший русин-митрополит Київський.', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1051, 0, 0, 0),
(82, 13, '&lt;p&gt;У цей період стрімко, і під патронатом князя, розвивалися законодавство, написання книг, освіта та дипломатія, але про це мова йтиме надалі і більш конкретно.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(83, 13, '&lt;p&gt;Наприкінці свого князювання, відчуваючи наближення смерті, надумав реформувати принцип престолонаслідування на Русі, адже досвід міжусобиць був не лише гірким і кривавим, але й повчальним.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 982, 0, 0, 0),
(84, 13, '20 лютого 1054 року Ярослав Володимирович помирає у Вишгороді.', 'Загальне твердження', 'Yaroslav-(981-1054)', 20, 2, 1054, 0, 0, 0),
(85, 13, '&lt;p&gt;Його було поховано у соборі Святої Софії, поряд із дружиною Інгігердою.&lt;/p&gt;\r\n', 'Загальне твердження', 'Yaroslav-(981-1054)', 0, 0, 1054, 0, 0, 0),
(86, 6, 'Почав своє правління, звісно, з питань внутрішніх. Захопивши до Києва Смоленськ та Любеч, маючи під собою Новгород, він обклав даниною древлян, полян, сіверян та радимичів.', 'Загальне твердження', 'Oleh-(845-912)', 0, 0, 882, 0, 0, 0),
(87, 3, 'Як єдина держава існувала з IX ст. до 30-х років XII ст.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(88, 3, 'Політичною формою була ранньофеодальна монархія з елементами федералізму.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(89, 3, 'Територія Київської Русі простягалася між Чорним і Балтійським морями, Карпатами і Волго-Окським межиріччям.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(90, 3, 'Основне місце в економіці посідало сільське госпо­дарство, де використовувалися різноманітні знаряддя праці - плуг, соха, сапа, серп, коса та ін. Вирощували жито, пшеницю, просо, ячмінь, овес, горох. Займалися також скотарством.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(91, 3, 'У Київській Русі були поширені різні ремесла; особливо були роз­винені чорна металургія й металообробка. Тоді вже виготовляли по­над 150 найменувань виробів із заліза.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(92, 3, 'Основними торговельними партнерами Київської Русі були Візантія, Волзька Болгарія, Хозарія, країни Арабського Сходу, Скандинавські та західноєвропейські країни. На міжнародні ринки Русь вивозила хутро, мед, шкіру, різноманітні ремісничі вироби. Ввозила золото, срібло, дорогі тканини, вина, посуд, зброю.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(93, 3, 'Уже за часів Володимира Святославича і Ярослава Мудрого Русь друкувала власні гроші, а в розрахунках у великих торговельних операціях використовувала срібні зливки - гривні.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(94, 3, 'У “Повісті временних літ” зазначалося, що в ІX–X ст. на Русі було 20 міст, у XI ст. - 32, а за іншими літописами у XIII ст. - понад 300 поселень міського типу.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(95, 4, 'зазначалося, що в ІX–X ст. на Русі було 20 міст, у XI ст. - 32, а за іншими літописами у XIII ст. - понад 300 поселень міського типу.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 1100, 0, 0, 0),
(96, 3, 'Главою держави був Великий київський князь. Правлячий клас становила його дружина (старша - бояри, молодша - звичайні дружинники, або гридні), через яку здійснювалось управління Руською землею.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(97, 3, 'Писемні джерела, у тому числі “Повість временних літ” літописця Нестора, свідчать про перші кроки східноєвропейського державництва ще в VI ст., коли в Середній Наддніпрянщині склався військово-політичний союз племен. Важливим моментом у цьому процесі стало заснування Києва (за останніми даними - у V ст.).', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 500, 0, 0, 0),
(98, 4, 'Писемні джерела, у тому числі “Повість временних літ” літописця Нестора, свідчать про перші кроки східноєвропейського державництва ще в VI ст., коли в Середній Наддніпрянщині склався військово-політичний союз племен.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 1100, 0, 0, 0),
(99, 4, 'Згідно з літописом першим київським князем був Кий. У VIII–IX ст. у Середній Наддніпрянщині виникло державне об’єднання - Руська земля, до складу якого ввійшли поляни, древляни, сіверяни.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 1100, 0, 0, 0),
(100, 3, 'Згідно з літописною традицією в генезисі Давньоруської держави активну роль відігравали нормани, яких на Русі називали варягами. Перші загони варягів з’явилися на слов’янській землі в IX ст.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(101, 3, 'Як записано у “Повісті временних літ”, у 862 р. варязького конунга Рюрика ільменські слов’яни запросили княжити в Новгороді на річці Волхові.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 862, 0, 0, 0),
(102, 4, 'Як записано у “Повісті временних літ”, у 862 р. варязького конунга Рюрика ільменські слов’яни запросили княжити в Новгороді на річці Волхові.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 862, 0, 0, 0),
(103, 3, 'У 882 р. один з варязьких проводирів Олег із загоном воїнів приплив з Новгорода до Києва. Князями Києва у той час були Аскольд і Дір Варяги назвали себе мирними купцями. Вони хитрощами заманили Аскольда і Діра до свого табору і там убили їх. Київським князем став Олег', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 882, 0, 0, 0),
(104, 3, 'Якщо князі Кий, Аскольд і Рюрик - герої легенд про Київ, то князь Олег (882-912 pp.) - перша історична особа в Київській Русі Відо­мо, що він як Великий київський князь збирав данину зі слов’янських племен, воював із хозарами.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 800, 0, 0, 1130),
(105, 3, 'У 907 і 911 р. князь Олег здійснив успішні походи на Візантію, завдяки чому Русь домоглася вигідних для себе мирних договорів із греками.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 907, 0, 0, 911),
(106, 3, 'Характер діяльності Київської Русі що склався за часів правління Олега, не змінився і при князеві Ігорі (912-945 pp.).', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 912, 0, 0, NULL),
(107, 7, 'У 941 р. Ігор здійснив похід на Візантію, у 944 р. у результаті воєнного походу до Закавказзя захопив Дербент, Ширван, столицю Уранії місто Бердаа.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 941, 0, 0, 0),
(108, 4, 'Повернувшись із Каспійського походу, Ігор вирішив сам зібрати підвищену дань з древлян. Раніше це була прерогатива його воєводи Свенельда. Переконавши древлян за допомогою своєї дружини віддати необхідні кошти, Ігор попрямував до Києва, але, відпустивши більшість свого війська, повернувся до Іскоростеня – головного міста древлян, аби зібрати данину для себе особисто. Це викликало обурення у древлян на чолі з їх ватажком Малом, і вирішили вони покінчити з цим свавіллям. Ось як описує це «Повість…».', 'Загальне твердження', 'Ihor-(bl-879945)', 0, 0, 945, 0, 0, 0),
(109, 3, 'У 941 р. Ігор здійснив похід на Візантію, у 944 р. у результаті воєнного походу до Закавказзя захопив Дербент, Ширван, столицю Уранії місто Бердаа.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 941, 0, 0, 0),
(110, 3, 'Під час правління Ігоря на південних кордонах Русі з’явилися печеніги. У 915 р. вони домовилися з Києвом про мир і відійшли до Дунаю, але вже в 930 р. Ігореві довелося воювати з ними.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 945, 0, 0, 0),
(111, 3, 'Часті війни, велика данина викликали невдоволення населення. Восени 945 р. позачергова поїздка Ігоря з дружиною за даниною до древлян завершилася трагічно - жителі міста Іскоростеня повстали й убили Ігоря ', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 945, 0, 0, 0),
(112, 3, 'Повстання древлян жорстоко придушила вдова Ігоря княгиня Ольга', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 945, 0, 0, 0),
(113, 3, 'Літописець Нестор записав про це легенду. Усіх представників першого посольства древлян Ольга наказала закопати живими в землю, друге посольство було спалене. Потім Ольга з дружиною прийшла до древлян нібито з миром і запросила знатних бояр до себе в табір на бенкет. Гостей напоїли вином і потім зарубали. А жителям Іскоростеня на знак обіцяного їм примирення княгиня наказала принести голубів -по одному з кожного дому. Дружинники Ольги прив’язали до лапок го­лубів клоччя, підпалили його і відпустили птахів. Голуби полетіли в місто і сіли під дахи осель. Іскоростень спалахнув і перетворився на попіл.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 945, 0, 0, 0),
(114, 8, 'Повстання древлян жорстоко придушила вдова Ігоря княгиня Ольга. Усіх представників першого посольства древлян Ольга наказала закопати живими в землю, друге посольство було спалене. Потім Ольга з дружиною прийшла до древлян нібито з миром і запросила знатних бояр до себе в табір на бенкет. Гостей напоїли вином і потім зарубали. А жителям Іскоростеня на знак обіцяного їм примирення княгиня наказала принести голубів -по одному з кожного дому. Дружинники Ольги прив’язали до лапок го­лубів клоччя, підпалили його і відпустили птахів. Голуби полетіли в місто і сіли під дахи осель. Іскоростень спалахнув і перетворився на попіл.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 945, 0, 0, 0),
(115, 3, 'Княгиня Ольга правила державою до повноліття свого сина Святослава.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 946, 0, 0, 1130),
(116, 3, 'За часів її правління (945-964 pp.) підвищився міжнародний авторитет Київської Русі Цьому сприяли візит Великої княгині Ольги до Константинополя і прийняття нею християнства. У ці часи розвивалися відносини з іншими державами. У внутрішній політиці Ольга здійснила реформи щодо упорядкування збирання данини.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 946, 0, 0, 1130),
(117, 9, 'У 964 р. Великим князем Київської Русі став син Ольги Святослав (964-972 pp.). Усе своє життя він провів у воєнних походах і битвах.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 964, 0, 0, 0),
(118, 9, ' Протягом 965-967 pp. Святослав розбив Хозарський каганат, Болгарське царство на Волзі, увів до складу Київської Русі в’ятичів. На Північному Кавказі йому підкорилися племена осетинів і черкесів. Завдяки перемогам Святослава Київська Русь значно розширилася. Руські купці дістали змогу торгувати з країнами Сходу.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 965, 0, 0, 967),
(119, 9, 'З 968 р. Святослав розпочав експансію на Дунай. Розбивши Болгарію, він захопив дунайські міста, а місто Переяславець-на-Дунаї зробив своєю резиденцією з наміром перенести сюди згодом і столицю Русі', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 968, 0, 0, 0),
(120, 3, 'У 964 р. Великим князем Київської Русі став син Ольги Святослав (964-972 pp.)', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 964, 0, 0, 0),
(121, 3, 'Протягом 965-967 pp. Святослав розбив Хозарський каганат, Болгарське царство на Волзі, увів до складу Київської Русі в’ятичів. На Північному Кавказі йому підкорилися племена осетинів і черкесів. Завдяки перемогам Святослава Київська Русь значно розширилася. Руські купці дістали змогу торгувати з країнами Сходу.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 965, 0, 0, 967);
INSERT INTO `Theses` (`id`, `concept_id`, `theses`, `class`, `page_code`, `day_start`, `month_start`, `year_start`, `day_end`, `month_end`, `year_end`) VALUES
(122, 3, 'З 968 р. Святослав розпочав експансію на Дунай. Розбивши Болгарію, він захопив дунайські міста, а місто Переяславець-на-Дунаї зробив своєю резиденцією з наміром перенести сюди згодом і столицю Русі Далі Великий князь почав боротьбу з Візантією за Балканські землі. Здобувши кілька перемог над візантійськими військами, він проник углиб Візантійської імперії.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 968, 0, 0, 0),
(123, 3, 'У 971 р. військо Святослава було оточене в місті Доростолі на Дунаї величезною армією імператора Йоанна Цимісхія. Нерівний бій тривав довго. Воїни Святослава не відступили. Візантійський імператор, переконавшись, що руське військо стоїть на смерть, вирішив укласти зі Святославом мир.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 971, 0, 0, 0),
(124, 3, 'У 972 р. Святослав з невеликою частиною дружини повертався до Києва. Поблизу дніпровських порогів на нього несподівано напали печеніги. У жорстокій битві Святослав загинув.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 972, 0, 0, 0),
(125, 3, 'Після смерті Святослава Великим князем Київської держави став його син Володимир (980-1015 pp.). Він очолив боротьбу проти печенігів, які дедалі більше загрожували Київській Русі', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 980, 0, 0, 0),
(126, 3, 'Протягом 981-993 pp. Володимир здійснив походи на ятвагів, в’ятичів, хорватів, по­ляків, у результаті чого завершився тривалий процес формування державної території Київської Русі', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 981, 0, 0, 993),
(127, 3, 'З метою зміцнення Київської Русі Володимир здійснив низку релігійних реформ. Спочатку він хотів піднести авторитет язичницьких богів і для цього збудував у Києві нове святилище - пантеон усіх слов’янських богів.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 982, 0, 0, 0),
(128, 3, 'Проте зрозумівши, що язичництво віджило своє, Володимир по­кінчив з ним. У 988 р. у Київській Русі офіційною державною релігією стає християнство (у його візантійсько-православному варіанті). Запрошені з Візантії священики відправляли обряд хрещення, часто примусово. Дерев’яні зображення язичницьких богів спалювали або кидали в річку.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 988, 0, 0, 0),
(129, 16, 'Християнство на державному рівні на Русі запровадив Володимир Святославович. Офіційно, на церковному рівні, вважається, що відбулося це у 988 році, хоча, очевидно, цей процес відбувався не один рік через величезний розмір держави та картатість населення.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(130, 16, 'У надважливому центрі Русі Новгороді, хрещення почалося лише 990 року, і тривало не один рік, так як північне населення Русі скептичніше ставилося до нової релігії.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 990, 0, 0, 0),
(131, 16, 'Хрещення відбувалося «вогнем і мечем», руйнувалися язичницькі капища. Відповідно, такі дії потребували підготовки, певного часу на виконання та ще більше часу на те, щоб примусово охрещена спільність припинила спротив і прийняла нову релігію.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(132, 16, 'Каста волхвів, служителів культів язичництва, користувалася неймовірною повагою у населення і деякий час після впровадження нової релігії мала значний вплив. І лише їх повне знищення, а скоріше – зникнення можна було вважати за перемогу нової релігії.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(133, 16, 'Християнство не було новинкою на Русі існують свідчення про «Аскольдове хрещення», Ольга офіційно прийняла християнство, община християн у Києві була доволі значною. ', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 990, 0, 0, 0),
(134, 16, '«Повість» пропонує легенду про вибір релігії Володимиром. Іслам не сподобався князю, адже забороняв алкоголь.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 990, 0, 0, 0),
(135, 4, '«Повість» пропонує легенду про вибір релігії Володимиром. Іслам не сподобався князю, адже забороняв алкоголь.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(136, 16, 'Представники католицизму майже не мали шансів, адже Володимир посилаючись на часи Ольги, коли католицька місія в Русь провалилася, також не пристав на доводи на користь «західного християнства».', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 990, 0, 0, 0),
(137, 4, 'Представники католицизму майже не мали шансів, адже Володимир посилаючись на часи Ольги, коли католицька місія в Русь провалилася, також не пристав на доводи на користь «західного християнства».', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(138, 16, 'Іудаїсти із Хозарського каганату також пропонували свою релігію. Проте Володимирові не сподобалася доля євреїв, котрих вигнали з їх Батьківщини.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 990, 0, 0, 0),
(139, 4, 'Іудаїсти із Хозарського каганату також пропонували свою релігію. Проте Володимирові не сподобалася доля євреїв, котрих вигнали з їх Батьківщини.', 'Загальне твердження', 'Pryynyattya-hrystyyanstva', 0, 0, 988, 0, 0, 0),
(140, 3, 'За часів правління Володимира крім Візантії Київська Русь підтримувала зв’язки з Німеччиною, Римом, Поль­щею, Чехією, Скандинавськими країнами.', 'Загальне твердження', 'Kyyivska-Rus', 0, 0, 989, 0, 0, 0),
(141, 3, 'Класична ранньофеодальна держава з монархічною формою правління.', 'Визначення', 'Politychnyy-ustriy', 0, 0, 799, 0, 0, 0),
(142, 4, 'Літописне зведення, складене в Києві на початку 12 століття, пам''ятка історіографії та літератури Київської Русі.', 'Визначення', 'Kyyivska-Rus', 0, 0, 1100, 0, 0, 0),
(143, 5, 'Бояри Рюрика, варяга і новгородського князя.', 'Визначення', 'Zasnuvannya-Kyyeva', 0, 0, 839, 0, 0, 0),
(144, 6, 'Довірена особа новгородського князя Рюрика і регент свого малолітнього сина – Ігоря', 'Визначення', 'Oleh-(845-912)', 0, 0, 845, 0, 0, 0),
(145, 9, 'Руський князь, державний діяч, політик і військовик з династії Рюриковичів. Великий князь Київський, правитель Русі (945, самостійно з 964–972).', 'Визначення', 'Svyatoslav-(934-972)', 0, 0, 934, 0, 0, 0),
(146, 10, 'Великий князь Київський (972-978), правитель Київської Русі. Старший син київського князя Святослава. Онук і вихованець княгині Ольги.', 'Визначення', 'Yaropolk-(956-980)', 0, 0, 956, 0, 0, 0),
(147, 11, 'Новгородський князь (970–988), Великий князь Київський (979–1015), правитель і хреститель Київської Русі.', 'Визначення', 'Volodymyr-(958-1015)', 0, 0, 958, 0, 0, 0),
(148, 12, 'Великий князь Київський (1015-1019). Син Володимира Великого (за іншою версією — Ярополка Святославича).', 'Визначення', 'Svyatopolk-(981-1019)', 0, 0, 981, 0, 0, 0),
(149, 13, 'Великий князь Київський (1015–1018, 1019–1054), святий. Був другим сином хрестителя Київської Русі князя Володимира Святославича з династії Рюриковичів.', 'Визначення', 'Yaroslav-(981-1054)', 0, 0, 981, 0, 0, 0),
(150, 16, 'Процес прийняття і поширення християнства у Русі.', 'Визначення', 'concept', 0, 0, 988, 0, 0, 0),
(151, 14, 'Великий князь Київський (1113–1125), державний і політичний діяч, письменник. Син князя Всеволода Ярославича і візантійської княжни Марії (за іншими даними — Анни) з роду Мономахів.', 'Визначення', 'Volodymyr-II-Monomah-(1053-1125)', 0, 0, 1053, 0, 0, 1125),
(152, 14, 'Славу та могутність отримав ще до входження на київський престол, будучи князем переяславським.', 'Загальне твердження', 'Volodymyr-II-Monomah-(1053-1125)', 0, 0, 1053, 0, 0, 1125),
(153, 14, 'Його князювання стало «лебединою піснею» Русі адже держава на короткий час наблизилася, хоча й не повернулася, до своєї могутності минулих років.', 'Загальне твердження', 'Volodymyr-II-Monomah-(1053-1125)', 0, 0, 1053, 0, 0, 1125);
